package janani;
import java.util.Scanner;
public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n,n1,n2,n3;
Scanner ja=new Scanner(System.in);
n1=ja.nextInt();
n2=ja.nextInt();
n3=ja.nextInt();
n=(n3/2)*((2*n1)+(n3-1)*n2);
System.out.println(n);
	}

}
